html{
    display:grid;
    width:100vw;
    height:100vh;
}


#root{
    display: grid;
    grid-template-rows: min-content min-content auto min-content;
    grid-template-areas:
    "navigation"
    "header"
    "content"
    "footer";
    margin: 0;
    padding: 0;
    min-height: 100vh;
    font-family: 'The Girl Next Door', cursive;
    background-color: rgba(235, 199, 152, 0.774);
}

a {
    text-decoration: none;
    color: rgb(88, 42, 34);
}


#navigation{
    grid-area: navigation;
    
    
}

#navigation ul{
    align-items: center;
    flex-flow: row nowrap;
    justify-content: space-around;
}

#navigation ul li {
    display: inline;
    font-size: 1.25em;
    list-style: none;

}

#header{
    grid-area: header;
}


@media (min-width: 700px) {
    #root{
    background-color: rgba(235, 199, 152, 0.774);
    grid-template-rows: min-content auto min-content;
    grid-template-areas: 
    "navigation"
    "header"
    "content"
    "footer";
    }
    #navigation > ul {
        flex-flow: row nowrap;
    }
}

#content {  
    grid-area: content;
    padding: 0;
}

#footer {
    grid-area: footer; 
    display: inline;
}

#footer ul {
    list-style: none;
    text-align: center;
}    
#footer ul li {
    display: inline;
    font-size: 1.25em;
    color: rgb(88, 42, 34);
    
}

.Poems{
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-direction: column;
    padding: 30px;
}

.Poems img {
    width: 65%;
    height: 75%;
    padding: 10px;
}

.Poems h2{
    font-style: italic;
}

.wrapper{
    /* height set to full view height */
    height: 100vh;
    /* disable horizontal scroll */
    overflow-x:hidden;
    /* enable scrolling on page*/
    overflow-y: auto;
    /* set perspective to 2px. distance from the vp to transformed objects*/
    perspective: 2px;
}

.section{
    position: relative;
    height: 100vh;

    /* text formatting*/
    display: flex;
    align-items: center;
    justify-content: center;
}

.parallax::after{
    /*display & position the pseudo- element*/
    content: "";
    position: absolute;
    top:0;
    right: 0;
    bottom:0;
    left:0;
    transform: translateZ(-1px) scale(1.5);
    background-size: 100%;
    z-index: -1;
}

.static{
    background:rgb(88, 42, 34);
}

.tiltedBackground {
    flex-flow: row nowrap;
    width: 100vw;
    height: 100vh;
    color:rgb(88, 42, 34);
    font-size: 20px;
    letter-spacing: 1px;
    background: url('https://thepracticalhistorian.files.wordpress.com/2018/10/bookstack.jpg');
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    align-content: center;
}

.quote{
    color: rgb(88, 42, 34);;
    text-shadow: white;
}


.edit {
    align-items: center;
    color: rgb(88, 42, 34);
}

.contain{
    width: 300px;
    height: 250px;
    float: left;
    padding: 5px;
}

.info{
    color: rgb(88, 42, 34);
}

.aboutAuthor h1{
    font-weight: bold;
}

.aboutAuthor h2{
    font-style: italic;
}

.aboutAuthor h3{
    font-style: italic;
}

.mapouter{
    padding: 5px;
    float: right;
}
