#root{
    display: grid;
    grid-template-rows: min-content min-content auto min-content;
    grid-template-areas:
    "navigation"
    "header"
    "content"
    "footer";
    margin: 0;
    padding: 0;
    min-height: 100vh;
    font-family: 'The Girl Next Door', cursive;
    background-color: rgba(235, 199, 152, 0.774);
}

a {
    text-decoration: none;
    color: rgb(141, 14, 14);
}


#navigation{
    grid-area: navigation;
      
}

#navigation ul{
    display: inline;
}

#navigation li {
    font-size: 20px;
    list-style: none;

}

#header{
    grid-area: header;
}

#content {  
    grid-area: content;
    
}



#footer {
    grid-area: footer; 
    position: relative;
}

#footer ul {
    display: inline;
    text-align: center;
    list-style: none;
}    
#footer ul li {
    font-size: 20px;
    
}

.tiltedBackground {
    background: url(https://thepracticalhistorian.files.wordpress.com/2018/10/bookstack.jpg)
    no-repeat center;
    width: 100vh;
    height: 100vh;
    
}

.intro {
    color:rgb(88, 42, 34);
    font-size: 20px;
    letter-spacing: 1px;    
}

.edit {
    margin: 0;
    padding: 0;
    color: blanchedalmond;
    background: url(http://www.steveheimoff.com/wp-content/uploads/2008/12/oaklandfog1.jpg);
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
}
