generated.css:
#root{
    display: grid;
    grid-template-rows: min-content min-content auto min-content;
    grid-template-areas:
    "navigation"
    "header"
    "content"
    "footer";
    margin: 0;
    padding: 0;
    min-height: 100vh;
    font-family: 'The Girl Next Door', cursive;
    background-color: rgba(235, 199, 152, 0.774);
}

a {
    text-decoration: none;
    color: rgb(141, 14, 14);
}


#navigation{
    grid-area: navigation;
    top: 0;
      
}

#navigation ul{
    align-items: flex-start;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: space-around;
}

#navigation ul li {
    display: inline;
    font-size: 20px;
    list-style: none;

}

#header{
    grid-area: header;
}

#content {  
    grid-area: content;
}

#footer {
    grid-area: footer; 
    display: inline;
    position: relative;
}

#footer ul {
    text-align: center;
    list-style: none;
}    
#footer ul li {
    font-size: 20px;
    
}

@media (min-width: 750px) {
    #root {
        font-family: 'The Girl Next Door', cursive;
        background-color: rgba(235, 199, 152, 0.774);
        grid-template-columns: 15% auto;
        grid-template-rows: min-content auto min-content;
        grid-template-areas: 
        "navigation header"  
        "navigation content" 
        "navigation footer";
    }
}

.Poems{
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-direction: column;
    padding: 30px;
}

.Poems img {
    width: 65%;
    height: 75%;
    padding: 10px;
}

.tiltedBackground {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    align-items: stretch;
    color:rgb(88, 42, 34);
    font-size: 20px;
    letter-spacing: 1px;
}

.tiltedBackground img{
    width: 90%;
    height: 70%;
    float: left;
}

.contain{
    object-fit: contain;
}


.edit {
    margin: 0;
    padding: 0;
    color: rgb(88, 42, 34);
}

.intro{
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    width:300px;
    height:450px;
    
}
