html{
    height: 100%;
    overflow: hidden;
}


#root{
    display: grid;
    grid-template-rows: min-content min-content auto min-content;
    grid-template-areas:
    "navigation"
    "header"
    "content"
    "footer";
    margin: 0;
    padding: 0;
    min-height: 100vh;
    font-family: 'The Girl Next Door', cursive;
    background-color: rgba(235, 199, 152, 0.774);
}

a {
    text-decoration: none;
    color: rgb(88, 42, 34);
}


#navigation{
    grid-area: navigation;
    
    
}

#navigation ul{
    align-items: center;
    flex-flow: row nowrap;
    justify-content: space-around;
}

#navigation ul li {
    display: inline;
    font-size: 1.25em;
    list-style: none;

}

#header{
    grid-area: header;
}


@media (min-width: 700px) {
    #root{
    background-color: rgba(235, 199, 152, 0.774);
    grid-template-rows: min-content auto min-content;
    grid-template-areas: 
    "navigation"
    "header"
    "content"
    "footer";
    }
    #navigation > ul {
        flex-flow: row nowrap;
    }
}

#content {  
    grid-area: content;
    padding: 0;
}

#footer {
    grid-area: footer; 
    display: inline;
}

#footer ul {
    list-style: none;
    text-align: center;
}    
#footer ul li {
    display: inline;
    font-size: 1.25em;
    color: rgb(88, 42, 34);
    
}

.Poems{
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-direction: column;
    padding: 30px;
}


.Poems h2{
    font-style: italic;
}

